function insertEmoticons() {
    var html = '';
    var counter = 0;
    emoticonList.forEach((emo) => {
        html = `<a class="emoticon__button" value="${emo}" id="add-emoticon${counter}">${emo}</a>`;
        jQuery('#chat__emoticons').append(html);
        jQuery(`#add-emoticon${counter}`).on('click', function (event) {
            var messageTextbox = jQuery('[name=message]');
            var text = messageTextbox.val();
            messageTextbox.val(text + event.target.text);
        });
        counter++;
    });
    counter = 0;
    animalList.forEach((emo) => {
        html = `<a class="emoticon__button" value="${emo}" id="add-animal-emoticon${counter}">${emo}</a>`;
        jQuery('#chat__animal__emoticons').append(html);
        jQuery(`#add-animal-emoticon${counter}`).on('click', function (event) {
            var messageTextbox = jQuery('[name=message]');
            var text = messageTextbox.val();
            messageTextbox.val(text + event.target.text);
        });
        counter++;
    });
    counter = 0;
    headList.forEach((emo) => {
        html = `<a class="emoticon__button" value="${emo}" id="add-head-emoticon${counter}">${emo}</a>`;
        jQuery('#chat__head__emoticons').append(html);
        jQuery(`#add-head-emoticon${counter}`).on('click', function (event) {
            var messageTextbox = jQuery('[name=message]');
            var text = messageTextbox.val();
            messageTextbox.val(text + event.target.text);
        });
        counter++;
    });

};
insertEmoticons();

var smileys = jQuery('#chat__emoticons');
var animals = jQuery('#chat__animal__emoticons');
var heads = jQuery('#chat__head__emoticons');

jQuery('#emoticons-smileys').on('click', function () {
    smileys.toggleClass("hidden");
    animals.addClass("hidden");
    heads.addClass("hidden");
});

jQuery('#emoticons-animal-smileys').on('click', function () {
    animals.toggleClass("hidden");
    smileys.addClass("hidden");
    heads.addClass("hidden");
});

jQuery('#emoticons-head-smileys').on('click', function () {
    heads.toggleClass("hidden");
    smileys.addClass("hidden");
    animals.addClass("hidden");
});

